import React, { useState } from "react";
import axios from "axios";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import { Text, View, TextInput, TouchableOpacity, Image,AsyncStorage  } from "react-native";

import { styles } from "./styleSheets.js";
import ResponsiveScreen from "react-native-auto-responsive-screen";
ResponsiveScreen.init(720, 1600);
const LoginPage = (props) => {
  // const [email, setEmail] = useState("");

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const onEChange = (textValue) => setEmail(textValue);
  const onPChange = (textValue) => setPassword(textValue);
  _storeData = async () => {
    try {
      await AsyncStorage.setItem(
        '@Mahdi:mahdi',
        'I like to save it.'
      );
    } catch (error) {
      // Error saving data
    }
  };
  _retrieveData = async () => {
    try {
      const value = await AsyncStorage.getItem('mahdi');
      if (value !== null) {
        // We have data!!
        console.log(value);
      }
    } catch (error) {
      // Error retrieving data
    }
  };
  const isStatus = 404;
  let mahdi = 'abdollah';
  const setSend = () => {
    // const response 
    () => props.navigation.navigate("OpenProject");

    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/USER/login/",
      headers: {
        // 'Content-Type': "application/json",
        // Authorization: `Token ${mahdi}`,
        // 'Accept': 'application/json'
      },
      data: {
        email: email,
        password: password,
      },
    })
      // .then((response) => console.log(response.status))
      .then((response) => {
        if (response.status == "202") {
          props.navigation.navigate("OpenProject");
          console.log(response.data.data.token);

          let mahdi = response.data.data.token
          console.log(mahdi)
        }
      })


      .catch((error) => console.log(error));
    }
  return (
    <View style={[styles.page, { flexDirection: "column" }]}>
      <View style={styles.box}>
        <View>
          <Image
            source={require("../assets/app_ui2-12.png")}
            style={styles.logo}
            resizeMode="cover"
          />
        </View>
        <View style={styles.fields}>
          <TextInput
            placeholder="Type your email address  "
            style={styles.text}
            onChangeText={onEChange}
          />
        </View>
        <View
          style={[styles.fields, { marginTop: ResponsiveScreen.normalize(20) }]}
        >
          <TextInput
            secureTextEntry={true}
            placeholder="password"
            style={styles.text}
            onChangeText={onPChange}
          />
        </View>
        <View style={styles.butbox}>
          <View>
            <TouchableOpacity
              style={[styles.Button, { flexDirection: "row" }]}
              onPress={setSend}
            >
              <Text style={styles.ButtonText}> Login</Text>
              {/* <Text style={{transform: [{ rotate: "90deg" }],fontSize:hp('2%'),color:"#fff"}}> ^ </Text> */}
            </TouchableOpacity>
          </View>
          <View>
            <TouchableOpacity
              style={[{ backgroundColor: "transparent" }]}
              onPress={setSend}
              >
              <Text
                style={[
                  styles.ButtonText,
                  {
                    marginTop: ResponsiveScreen.normalize(10),
                    marginLeft: ResponsiveScreen.normalize(20),
                  },
                ]}
                onPress={() => props.navigation.navigate("ForgetPass")}
                >

                Forgot Password
              </Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
      <View
        style={{
          height: ResponsiveScreen.normalize(530),
          alignItems: "center",
        }}
      >
        <Text
          style={{
            marginTop: ResponsiveScreen.normalize(210),
            color: "white",
            fontSize: ResponsiveScreen.normalize(30),
            // marginLeft: "23%",
          }}
        >
          Client Application
        </Text>
        <Text
          style={{
            marginTop: ResponsiveScreen.normalize(20),
            marginBottom: ResponsiveScreen.normalize(320),
            color: "white",
            fontSize: ResponsiveScreen.normalize(20),
          }}
        >
          All rights reserved by E-novation engineering Co.{" "}
        </Text>
      </View>
    </View>
  );
};

export default LoginPage;

